// services/websocketService.js - Frontend (Complete)
import { io } from 'socket.io-client';

class WebSocketService {
  constructor() {
    this.socket = null;
    this.isConnected = false;
    this.reconnectAttempts = 0;
    this.maxReconnectAttempts = 5;
    this.reconnectDelay = 1000;
    this.currentConversationId = null;
    this.eventCallbacks = new Map();
    this.authToken = null;
  }

  // K·∫øt n·ªëi WebSocket
  connect(serverUrl = 'http://localhost:3000') {
    if (this.socket) {
      this.disconnect();
    }

    // L·∫•y token t·ª´ state management (thay v√¨ localStorage)
    const token = this.getAuthToken();
    if (!token) {
      console.error('‚ùå No auth token found');
      this.emit('auth_error', { message: 'No authentication token' });
      return;
    }

    console.log('üîå Connecting to WebSocket server:', serverUrl);

    this.socket = io(serverUrl, {
      auth: {
        token: token
      },
      autoConnect: true,
      reconnection: true,
      reconnectionAttempts: this.maxReconnectAttempts,
      reconnectionDelay: this.reconnectDelay,
      timeout: 10000
    });

    this.setupEventListeners();
  }

  // L·∫•y auth token (c·∫ßn implement theo state management c·ªßa b·∫°n)
  getAuthToken() {
    // Thay th·∫ø b·∫±ng c√°ch l·∫•y token t·ª´ state management
    // V√≠ d·ª•: return store.getState().auth.token;
    // Ho·∫∑c t·ª´ context: return authContext.token;
    
    // T·∫°m th·ªùi s·ª≠ d·ª•ng bi·∫øn trong memory thay v√¨ localStorage
    return this.authToken || null;
  }

  // Set auth token
  setAuthToken(token) {
    this.authToken = token;
  }

  // Thi·∫øt l·∫≠p c√°c event listeners
  setupEventListeners() {
    if (!this.socket) return;

    // Connection events
    this.socket.on('connect', () => {
      console.log('‚úÖ WebSocket connected:', this.socket.id);
      this.isConnected = true;
      this.reconnectAttempts = 0;
      this.emit('connected', { socketId: this.socket.id });
    });

    this.socket.on('disconnect', (reason) => {
      console.log('‚ùå WebSocket disconnected:', reason);
      this.isConnected = false;
      this.emit('disconnected', { reason });
    });

    this.socket.on('connect_error', (error) => {
      console.error('‚ùå WebSocket connection error:', error.message);
      this.isConnected = false;
      this.emit('connection_error', { error: error.message });
    });

    this.socket.on('reconnect', (attemptNumber) => {
      console.log('üîÑ WebSocket reconnected after', attemptNumber, 'attempts');
      this.isConnected = true;
      this.emit('reconnected', { attemptNumber });
      
      // Rejoin current conversation if exists
      if (this.currentConversationId) {
        this.joinConversation(this.currentConversationId);
      }
    });

    this.socket.on('reconnect_failed', () => {
      console.error('‚ùå WebSocket reconnection failed');
      this.isConnected = false;
      this.emit('reconnection_failed');
    });

    // Authentication events
    this.socket.on('connected', (data) => {
      console.log('üéâ Authentication successful:', data);
      this.emit('authenticated', data);
    });

    // Conversation events
    this.socket.on('joined_conversation', (data) => {
      console.log('üè† Joined conversation:', data);
      this.currentConversationId = data.conversationId;
      this.emit('joined_conversation', data);
    });

    this.socket.on('left_conversation', (data) => {
      console.log('üö™ Left conversation:', data);
      if (this.currentConversationId === data.conversationId) {
        this.currentConversationId = null;
      }
      this.emit('left_conversation', data);
    });

    // Message events
    this.socket.on('new_message', (data) => {
      console.log('üì® New message received:', data);
      this.emit('new_message', data);
    });

    this.socket.on('ai_response', (data) => {
      console.log('ü§ñ AI response received:', data);
      this.emit('ai_response', data);
    });

    // User activity events
    this.socket.on('typing', (data) => {
      console.log('‚å®Ô∏è Typing indicator:', data);
      this.emit('typing', data);
    });

    this.socket.on('user_joined', (data) => {
      console.log('üëã User joined:', data);
      this.emit('user_joined', data);
    });

    this.socket.on('user_left', (data) => {
      console.log('üö∂ User left:', data);
      this.emit('user_left', data);
    });

    // Online users events
    this.socket.on('online_users', (data) => {
      console.log('üë• Online users updated:', data);
      this.emit('online_users', data);
    });

    // File upload events
    this.socket.on('file_uploaded', (data) => {
      console.log('üìÅ File uploaded:', data);
      this.emit('file_uploaded', data);
    });

    // Conversation update events
    this.socket.on('conversation_updated', (data) => {
      console.log('üîÑ Conversation updated:', data);
      this.emit('conversation_updated', data);
    });

    // System events
    this.socket.on('system_message', (data) => {
      console.log('üì¢ System message:', data);
      this.emit('system_message', data);
    });

    // Error events
    this.socket.on('error', (data) => {
      console.error('‚ùå WebSocket error:', data);
      this.emit('error', data);
    });
  }

  // Ng·∫Øt k·∫øt n·ªëi WebSocket
  disconnect() {
    if (this.socket) {
      console.log('üîå Disconnecting WebSocket...');
      this.socket.disconnect();
      this.socket = null;
    }
    this.isConnected = false;
    this.currentConversationId = null;
    this.eventCallbacks.clear();
  }

  // Tham gia conversation
  joinConversation(conversationId) {
    if (!this.socket || !this.isConnected) {
      console.warn('‚ö†Ô∏è WebSocket not connected');
      return false;
    }

    if (!conversationId) {
      console.error('‚ùå Conversation ID is required');
      return false;
    }

    console.log('üè† Joining conversation:', conversationId);
    this.socket.emit('join_conversation', { conversationId });
    return true;
  }

  // R·ªùi kh·ªèi conversation
  leaveConversation(conversationId) {
    if (!this.socket || !this.isConnected) {
      console.warn('‚ö†Ô∏è WebSocket not connected');
      return false;
    }

    if (!conversationId) {
      conversationId = this.currentConversationId;
    }

    if (!conversationId) {
      console.error('‚ùå No conversation to leave');
      return false;
    }

    console.log('üö™ Leaving conversation:', conversationId);
    this.socket.emit('leave_conversation', { conversationId });
    return true;
  }

  // G·ª≠i tin nh·∫Øn
  sendMessage(conversationId, content, role = 'user') {
    if (!this.socket || !this.isConnected) {
      console.warn('‚ö†Ô∏è WebSocket not connected');
      return false;
    }

    if (!conversationId || !content || content.trim() === '') {
      console.error('‚ùå Conversation ID and content are required');
      return false;
    }

    console.log('üì§ Sending message to conversation:', conversationId);
    this.socket.emit('send_message', {
      conversationId,
      content: content.trim(),
      role
    });
    return true;
  }

  // G·ª≠i typing indicator
  sendTyping(conversationId, isTyping) {
    if (!this.socket || !this.isConnected) {
      return false;
    }

    if (!conversationId) {
      conversationId = this.currentConversationId;
    }

    if (!conversationId) {
      return false;
    }

    this.socket.emit('typing', {
      conversationId,
      isTyping
    });
    return true;
  }

  // Th√¥ng b√°o file upload
  notifyFileUploaded(conversationId, fileInfo) {
    if (!this.socket || !this.isConnected) {
      console.warn('‚ö†Ô∏è WebSocket not connected');
      return false;
    }

    if (!conversationId || !fileInfo) {
      console.error('‚ùå Conversation ID and file info are required');
      return false;
    }

    console.log('üìÅ Notifying file upload:', fileInfo);
    this.socket.emit('file_uploaded', {
      conversationId,
      file: fileInfo
    });
    return true;
  }

  // L·∫•y danh s√°ch users online
  getOnlineUsers(conversationId) {
    if (!this.socket || !this.isConnected) {
      console.warn('‚ö†Ô∏è WebSocket not connected');
      return false;
    }

    if (!conversationId) {
      conversationId = this.currentConversationId;
    }

    if (!conversationId) {
      console.error('‚ùå Conversation ID is required');
      return false;
    }

    console.log('üë• Getting online users for conversation:', conversationId);
    this.socket.emit('get_online_users', { conversationId });
    return true;
  }

  // ƒêƒÉng k√Ω event callback
  on(event, callback) {
    if (!this.eventCallbacks.has(event)) {
      this.eventCallbacks.set(event, new Set());
    }
    this.eventCallbacks.get(event).add(callback);

    // Return function ƒë·ªÉ unsubscribe
    return () => {
      const callbacks = this.eventCallbacks.get(event);
      if (callbacks) {
        callbacks.delete(callback);
        if (callbacks.size === 0) {
          this.eventCallbacks.delete(event);
        }
      }
    };
  }

  // H·ªßy ƒëƒÉng k√Ω event callback
  off(event, callback) {
    const callbacks = this.eventCallbacks.get(event);
    if (callbacks) {
      callbacks.delete(callback);
      if (callbacks.size === 0) {
        this.eventCallbacks.delete(event);
      }
    }
  }

  // Emit event t·ªõi callbacks ƒë√£ ƒëƒÉng k√Ω
  emit(event, data) {
    const callbacks = this.eventCallbacks.get(event);
    if (callbacks) {
      callbacks.forEach(callback => {
        try {
          callback(data);
        } catch (error) {
          console.error(`Error in callback for event ${event}:`, error);
        }
      });
    }
  }

  // Ki·ªÉm tra tr·∫°ng th√°i k·∫øt n·ªëi
  isSocketConnected() {
    return this.socket && this.isConnected;
  }

  // L·∫•y th√¥ng tin socket hi·ªán t·∫°i
  getSocketInfo() {
    if (!this.socket) {
      return null;
    }

    return {
      id: this.socket.id,
      connected: this.isConnected,
      currentConversation: this.currentConversationId,
      reconnectAttempts: this.reconnectAttempts
    };
  }

  // Retry k·∫øt n·ªëi th·ªß c√¥ng
  retryConnection() {
    if (this.socket && !this.isConnected) {
      console.log('üîÑ Manual retry connection...');
      this.socket.connect();
    }
  }

  // Cleanup khi component unmount
  cleanup() {
    this.disconnect();
    this.eventCallbacks.clear();
    this.authToken = null;
    this.currentConversationId = null;
  }
}

// T·∫°o singleton instance
const websocketService = new WebSocketService();

export default websocketService;